import Paper from "../Generic/Paper"
import Typography from "../Generic/Typography"
import Button from "../Generic/Button"

const Card = (props) => {

    const { image, title, caption, subtitle, summary, children } = props

    return (
        <Paper sx={{ padding: '0px', overflow: 'hidden' }}>
             {image && <div style={{ backgroundimage: `url('${image}')`, backgroundColor: 'cyan'}} className='h-24 p-0'>

             </div>}
             <div className='p-4 flex flex-col'>
                <div className=''>
                    <div className='flex items-end'>
                        {title && <div>
                            <Typography variant='h1'>
                                {title}
                            </Typography>
                        </div>}
                        {caption && <div>
                            <Typography variant='h3'>
                                {caption}
                            </Typography>
                        </div>}
                    </div>
                </div>
                {subtitle && <div>
                    <Typography variant='subtitle'>
                        {subtitle.map((sub, index) => (
                            `${index !== 0 ? ' / ' : ''}${sub}`
                        ))}
                    </Typography>
                </div>}
                {children}
                <div className='flex py-3'>
                    <Button variant='outlined' className='rounded-none rounded-l-lg'>❤️</Button>
                    <Button className='rounded-none rounded-r-lg'>View</Button>
                </div>
             </div>
        </Paper>
    )
}

export default Card
