import { useTheme } from "../../Context/Theme"
import { useState } from 'react'

const Button = (props) => {

    const theme = useTheme()

    const [style, setStyle] = useState

    props.variant === 'outlined'
    ? 
    setStyle({ backgroundColor: 'transparent', color: theme.secondary, borderColor: 'transparent', ...props.sx })
    :
    setStyle({ backgroundColor: theme.primary, color: theme.secondary, borderColor: 'transparent', ...props.sx })

    return (
        <button style={style} className='rounded-full py-1 px-3'>
            {props.children}
        </button>
    )
}

export default Button
