import { useTheme } from "../../Context/Theme"
import {useState } from "react"

const Rating = (props) => {

    const { disabled, rate } = props

    const theme = useTheme()

    const [inputRating, setInputRating] = useState(0)

    return (
        <div className='flex'>
            <button onMouseLeave={() => setInputRating(0)}> onMouseEnter={() => {setInputRating(1)}} disabled={disabled} style={{ height: 'auto', width: 'auto', fontSize: '1.5em', color: rate >= 0.2 && inputRating > 0 ? theme.rating : theme.disabled }} className='rounded-full w-6 h-6'>★</button>
            <button onMouseEnter={() => {setInputRating(2)}} disabled={disabled} style={{ height: 'auto', width: 'auto', fontSize: '1.5em', color: rate >= 0.4 && inputRating > 1 ? theme.rating : theme.disabled }} className='rounded-full w-6 h-6'>★</button>
            <button onMouseEnter={() => {setInputRating(3)}} disabled={disabled} style={{ height: 'auto', width: 'auto', fontSize: '1.5em', color: rate >= 0.6 && inputRating > 2 ? theme.rating : theme.disabled }} className='rounded-full w-6 h-6'>★</button>
            <button onMouseEnter={() => {setInputRating(4)}} disabled={disabled} style={{ height: 'auto', width: 'auto', fontSize: '1.5em', color: rate >= 0.8 && inputRating > 3 ? theme.rating : theme.disabled }} className='rounded-full w-6 h-6'>★</button>
            <button onMouseEnter={() => {setInputRating(5)}} disabled={disabled} style={{ height: 'auto', width: 'auto', fontSize: '1.5em', color: rate === 1 && inputRating > 4 ? theme.rating : theme.disabled }} className='rounded-full w-6 h-6'>★</button>
        </div>
    )
}

export default Rating
