import { useState } from "react"

const Multislider = (props) => {

    const { type } = props

    const [sliderPos, setSliderPos] = useState([0,0])

    const sliderStyle = {
        position: 'absolute',
        webkitAppearance: 'none',
        mozAppearance: 'none',
        appearance: 'none',
        backgroundColor: 'transparent',
        pointerEvents: 'none',
    }

    const handleChange = (e) => {
        const { value, id } = e.target 
        setSliderPos([id == 0 ? value : sliderPos[0], id == 1 ? value : sliderPos[1]])
    }

    return (
        <>
            <div className='bg-slate-400 w-full h-1 rounded' style={{ position: 'relative', top: '-24px' }}></div>
            <div className='bg-blue-600 h-1 rounded' style={{ position: 'relative', top: '-28px', left: `${sliderPos[0]}%` }}></div>
            <input type='range' min='1' max='100' value={sliderPos[0]} id='0' onChange={handleChange} style={sliderStyle}></input>
            <input type='range' min='1' max='100' value={sliderPos[1]} id='1' onChange={handleChange} style={sliderStyle}></input>
        </>
    )
}

export default Multislider
