import { useTheme } from "../../Context/Theme"
import Button from "../Generic/Button"
import Multislider from "../Generic/Multislider"
import Paper from "../Generic/Paper"
import { useState, useRef, useEffect } from 'react'

const Dropdown = (props) => {

    const theme = useTheme()

    const [visible, setVisible] = useState(false)
    const [buttonPos, setButtonPos] = useState([0,0])
    const [endIcon, setEndIcon] = useState('▼')

    useEffect(() => {
        console.log(ref.current)
        // setButtonPos([e.target.offsetLeft, e.target.offsetTop + e.target.clientHeight]);
    }, [ref.current])

    const close = (e) => {
        const { classList } = e.target
        if (classList.contains('close-trigger')) {
            document.querySelectorAll('.close-trigger').forEach((el) => el.classList.add('fade-out'))
            setTimeout(() => {
                setVisible(false)
                setEndIcon('▼')
            }, 250)
        }
    }

    const Panel = (props) => {
        if (props.visible) {
            return (
                <div onClick={close} className='close-trigger' style={{ position: 'absolute', height: '100vh', width: '100vw', top: '0px', left: '0px' }}>
                    <div style={{position: 'absolute', left: `${buttonPos[0]}px`, top: `${buttonPos[1]}px` }} className="fade-in w-64 p-2 mt-1 bg-white border border-gray-200 rounded-md drop-shadow-2xl dark:bg-gray-800 dark:border-gray-700">
                        <div className='flex flex-row justify-between content-center'>
                            <p className='text-2xl font-bold'>{props.label}</p>
                            <Button className='close-trigger' onClick={close} variant='text' sx={{ backgroundColor: 'white', color: theme.primary }}>X</Button>
                        </div>
                        {props.children}
                    </div>
                </div>
            )
        }
    }

    return (
        <>
            <Button ref={ref} endIcon={endIcon} startIcon='t' variant='outlined' onClick={(e) => {setVisible(!visible)}}>
                {props.label}
            </Button>
            <Panel visible={visible} label={props.label} children={props.children} />
        </>
    )
}

export default Dropdown
