import { useState } from "react"

const Multislider = (props) => {

    const { type } = props

    const [sliderVal, setSliderVal] = useState(0)
    const [sliderPos, setSliderPos] = useState(0)

    const sliderStyle = {
        position: 'absolute',
        webkitAppearance: 'none',
        mozAppearance: 'none',
        appearance: 'none',
        backgroundColor: 'transparent',
        pointerEvents: 'none',
    }

    return (
        <>
            <div className='bg-slate-400 w-full h-1 rounded' style={{ position: 'relative', top: '-24px' }}></div>
            <div className='bg-blue-600 h-1 rounded' style={{ position: 'relative', top: '-28px' }}></div>
            <input type='range' min='1' max='100' style={sliderStyle}></input>
            <input type='range' min='1' max='100' style={sliderStyle}></input>
        </>
    )
}

export default Multislider
