import { useState } from "react"

const Multislider = (props) => {

    const { type } = props

    //weird weird glitch man,,,, no clue........ too tired to even comprehend,,,,,, cant use [0,100]
    const [sliderPos, setSliderPos] = useState([10,99])

    const sliderStyle = {
        position: 'absolute',
        webkitAppearance: 'none',
        mozAppearance: 'none',
        appearance: 'none',
        backgroundColor: 'transparent',
        pointerEvents: 'none',
    }

    const handleChange = (e) => {
        const { value, id } = e.target
        if ((id == 0 & value < sliderPos[1]-8) || (id == 1 & value-8 > sliderPos[0])) {
            setSliderPos([id == 0 ? value : sliderPos[0], id == 1 ? value : sliderPos[1]])
        }
    }

    return (
        <>
            <div className='bg-slate-400 w-full h-1 rounded' style={{ position: 'relative', width: 'calc(100% - 15px)' }}></div>
            <div className='bg-blue-600 h-1 rounded' style={{ position: 'relative', top: '12px', left: `${-2+sliderPos[0]*2}px`, width: `${-8+(sliderPos[1]-sliderPos[0])*2}px` }}></div>
            <div>
                <input type='range' min='10' max='99' value={sliderPos[0]} id='0' onChange={handleChange} style={sliderStyle}></input>
                <input type='range' min='10' max='99' value={sliderPos[1]} id='1' onChange={handleChange} style={sliderStyle}></input>
            </div>
        </>
    )
}

export default Multislider
