import { useState, useEffect } from "react"
import Typography from "./Typography"

const Multislider = (props) => {

    const { type, range } = props

    //weird weird glitch man,,,, no clue........ too tired to even comprehend,,,,,, cant use [0,100]
    const [sliderPos, setSliderPos] = useState(range ? range : [1,100])

    const sliderStyle = {
        position: 'absolute',
        webkitAppearance: 'none',
        mozAppearance: 'none',
        appearance: 'none',
        backgroundColor: 'transparent',
        pointerEvents: 'none',
        width: '88%',
    }

    const handleSliderChange = (e) => {
        const { value, id } = e.target
        if ((id == 0 & parseInt(value) < sliderPos[1]) || (id == 1 & parseInt(value) > sliderPos[0])) {
            setSliderPos([id == 0 ? parseInt(value) : sliderPos[0], id == 1 ? parseInt(value) : sliderPos[1]])
        }
    }

    return (
        <>
            <Typography variant='h3'>Between: {sliderPos[0]} - {sliderPos[1]}</Typography>
            <div className='bg-slate-400 w-full h-1 rounded' style={{ position: 'relative', top: '16px', width: 'calc(100% - 15px)' }}></div>
            <div className='bg-blue-600 h-1 rounded' style={{ position: 'relative', top: '12px', left: `${parseInt(sliderPos[0])-parseInt(range ? range[0] : 100)}px`, width: `${(parseInt(sliderPos[1])-parseInt(range ? range[0] : 100))-(parseInt(sliderPos[0])-parseInt(range ? range[0] : 100))}px` }}></div>
            <div>
                <input step='1' type='range' min={range ? range[0] : 1} max={range ? range[1] : 100} value={sliderPos[0]} id='0' onChange={handleSliderChange} style={sliderStyle}></input>
                <input step='1' type='range' min={range ? range[0] : 1} max={range ? range[1] : 100} value={sliderPos[1]} id='1' onChange={handleSliderChange} style={sliderStyle}></input>
            </div>
            <div className='h-8'></div>
        </>
    )
}

export default Multislider
