import { useTheme } from "../../Context/Theme"

const Button = (props) => {

    const theme = useTheme()

    const style = props.sx

    if (props.variant === 'outlined') {
        style = { backgroundColor: 'transparent', color: theme.secondary, borderColor: 'transparent', ...props.sx }
    }

    if (props.variant === 'outli') {
        style = { backgroundColor: theme.primary, color: theme.secondary, borderColor: 'transparent', ...props.sx }
    }

    return (
        <button style={style} className='rounded-full py-1 px-3'>
            {props.children}
        </button>
    )
}

export default Button
