import { useTheme } from "../../Context/Theme"
import Button from "../Generic/Button"
import Multislider from "../Generic/Multislider"
import Paper from "../Generic/Paper"
import { useState } from 'react'

const Dropdown = (props) => {

    const theme = useTheme()

    const [visible, setVisible] = useState(true)
    const [buttonPos, setButtonPos] = useState([0,0])

    const Panel = (props) => {
        if (props.visible) {
            return (
                <div onClick={(e) => {e.target.className === 'barrier' && props.setVisible(false)}} className='barrier' style={{ position: 'absolute', height: '100vh', width: '100vw', top: '0px', left: '0px' }}>
                    <div style={{position: 'absolute', left: buttonPos[0], top: buttonPos[1] }} className="w-64 p-2 bg-white border border-gray-200 rounded-md drop-shadow-2xl dark:bg-gray-800 dark:border-gray-700">
                        <div className='flex flex-row justify-between content-center'>
                            <p className='text-2xl font-bold'>{props.type}</p>
                            <Button variant='outlined' sx={{ backgroundColor: 'white', color: theme.primary }}>X</Button>
                        </div>
                        {props.children}
                    </div>
                </div>
            )
        }
    }

    return (
        <>
            <Button variant='outlined' onClick={(e) => {setVisible(!visible); setButtonPos([e.target.offsetLeft, e.target.offsetTop]); console.log(e.target.offsetLeft)}}>
                {props.type}
            </Button>
            <Panel visible={visible} setVisible={setVisible} />
        </>
    )
}

export default Dropdown
