import { useTheme } from "../../Context/Theme"

const Button = (props) => {

    const theme = useTheme()

    props.outlined
    ? 
    style = { backgroundColor: transparent, color: theme.secondary, borderColor: theme.primary, ...props.sx }
    :
    style = { backgroundColor: theme.primary, color: theme.secondary, borderColor: theme.primary, ...props.sx }

    return (
        <button style={style} className='rounded-full py-1 px-3'>
            {props.children}
        </button>
    )
}

export default Button
