const Popover = (props) => {

    const close = (e) => {
        const { classList } = e.target
        if (classList.contains('close-trigger')) {
            document.querySelectorAll('.close-trigger').forEach((el) => el.classList.add('fade-out'))
            setTimeout(() => {
                props.onClose()
            }, 200)
        }
    }

    return (
        <div onClick={close} className='close-trigger justify-center items-center' style={{ display: props.visible ? 'flex' : 'none', position: 'absolute', backgroundColor: 'rgba(0,0,0,0.5)', backdropFilter: 'blur(5px)', height: '100vh', width: '100vw', top: '0px', left: '0px' }}>
            <div className="fade-in w-2/3 p-2 mt-1 bg-white border border-gray-200 rounded-md drop-shadow-2xl">
                {props.children}
            </div>
        </div>
    )
}

export default Popover
