import Paper from "../Generic/Paper"
import Rating from "../Generic/Rating"
import Typography from "../Generic/Typography"
import Button from "../Generic/Button"
import { useTheme } from "../../Context/Theme"

const Card = (props) => {

    const { image, title, rating, popularity, caption, subtitle, summary, children } = props

    const theme = useTheme()

    return (
        <Paper sx={{ padding: '0px', overflow: 'hidden' }}>
             {image && <div style={{ backgroundimage: `url('${image}')`, backgroundColor: 'cyan'}} className='h-72 p-0'>

             </div>}
             <div className='p-4 flex flex-col'>
                <div className='flex justify-between w-full items-end'>
                    <div>
                        <div className='flex items-end'>
                            {title && <div>
                                <Typography variant='h1'>
                                    {title}
                                </Typography>
                            </div>}
                            {caption && <div>
                                <Typography variant='h3'>
                                    {caption}
                                </Typography>
                            </div>}
                        </div>
                        {subtitle && <div>
                            <Typography variant='subtitle'>
                                {subtitle.map((sub, index) => (
                                    `${index !== 0 ? ' / ' : ''}${sub}`
                                ))}
                            </Typography>
                        </div>}
                    </div>
                </div>
                {children}
                <div className='flex py-3'>
                    <Button variant='outlined' className='rounded-none rounded-l-full'>‚ù§Ô∏è</Button>
                    <Button className='rounded-none rounded-r-full'>View</Button>
                </div>
                <div className='flex flex-col'>
                    {rating && <div>
                        <Rating disabled rate={0.4} />
                    </div>}
                    {popularity && <div>
                        <Typography sx={{ color: theme.primary }} variant='h3'>
                            üçø #{popularity}
                        </Typography>
                    </div>}
                </div>
             </div>
        </Paper>
    )
}

export default Card
