import { useTheme } from "../../Context/Theme"
import { useState } from 'react'

const Button = (props) => {

    const theme = useTheme()

    const [style, setStyle] = useState({ backgroundColor: 'transparent', color: theme.primary, borderColor: theme.primary, borderWidth: '2px', ...props.sx })

    // props.variant === 'outlined' && setStyle({ backgroundColor: 'transparent', color: theme.secondary, borderColor: 'transparent', ...props.sx })

    return (
        <button style={style} className='rounded-full py-1 px-3'>
            {props.children}
        </button>
    )
}

export default Button
