import { useTheme } from "../../Context/Theme"
import { useState } from 'react'
import { useEffect } from "react/cjs/react.production.min"

const Button = (props) => {

    const theme = useTheme()

    const [style, setStyle] = useState({ backgroundColor: 'transparent', color: theme.primary, borderColor: theme.primary, borderWidth: '1px', ...props.sx })

    useEffect(() => {
        props.variant === 'outlined' && setStyle({ backgroundColor: 'transparent', color: theme.secondary, borderColor: 'transparent', ...props.sx })
    }, theme)

    return (
        <button style={style} className='rounded-full py-1 px-3'>
            {props.children}
        </button>
    )
}

export default Button
