import Button from "../Generic/Button"
import Multislider from "../Generic/Multislider"
import Paper from "../Generic/Paper"

const Dropdown = (props) => {

    const Panel = () => (
        <div class="w-64 p-2 bg-white border border-gray-200 rounded-md dark:bg-gray-800 dark:border-gray-700">
            <div className='flex flex-row justify-between content-center'>
                <p className='text-2xl font-bold'>{props.type}</p>
                <Button sx={{ color: 'secondary' }}>X</Button>
            </div>
            {props.children}
        </div>
    )

    return (
        <>
            <Button
            sx={{ backgroundColor: 'white', color: 'primary' }}
            >
                {props.type}
            </Button>
            <Panel />
        </>
    )
}

export default Dropdown
