import Button from "../Components/Generic/Button"
import Paper from "../Components/Generic/Paper"
import Typography from "../Components/Generic/Typography"
import { useTheme } from "../Context/Theme"
import { useState, useEffect } from "react"
import { getHeroes } from "../Api/MovieAPI"

const Home = () => {

    const theme = useTheme()

    const [heroes, setHeroes] = useState([])
    const [currHero, setCurrHero] = useState(0)

    useEffect(() => {
        getHeroes('w780').then(res => {
            console.log(res)
            setHeroes(res)
        })
    },[])

    setTimeout(() => {
        currHero < heroes.length - 1 ? setCurrHero(currHero + 1) : setCurrHero(0)
    },3500)

    return (
        <div className='flex flex-col h-screen' style={{ backgroundImage: `url(${heroes[currHero]})`, backgroundSize: 'cover', transition: 'all 4s', backdropFilter: 'brightness(0.1) opacity(0)' }}>
            <div className='flex grow justify-center pt-56'>
                <Paper sx={{ height: '206px', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '16px' }}>
                    <Typography variant='h1'>Movie Browser.</Typography>
                    <div className='flex'>
                        <input placeholder='Title' type='text' style={{ backgroundColor: 'transparent', borderColor: theme.primary, borderWidth: '1px' }} className='pl-2 rounded-none rounded-l-full w-72' />
                        <Button className='rounded-none rounded-r-full'>üîç</Button>
                    </div>
                    <div className='flex'>
                        <Typography sx={{ paddingTop: '1px' }}>Cant think of a movie? Feel free to browse</Typography>
                        <Button variant='text' sx={{ padding: '0px 0px 6px 2px' }}>here.</Button>
                        <div className='grow'></div>
                    </div>
                </Paper>
                <div style={{ position: 'fixed', width: '100vh', height: '100vh' }}>

                </div>
            </div>
        </div>
    )
}

export default Home
